-- Name: insert_discussionhead_with_tag(character varying, character varying, integer, character varying[]); Type: FUNCTION; Schema: public; Owner: me
--

CREATE FUNCTION public.insert_discussionhead_with_tag(param_content character varying, param_userid character varying, param_animeid integer, param_tags character varying[]) RETURNS public.discussionheads
    LANGUAGE plpgsql
    AS $$ 
DECLARE
	new_discussionheadid int4;
	discussionhead_row record;
BEGIN
	INSERT INTO discussionheads ( contentofdiscussion, starttime, userid )
	VALUES
		( param_content, LOCALTIMESTAMP, param_userid ) RETURNING discussionheadid INTO new_discussionheadid;
	
	FOR I IN 1..CARDINALITY ( param_tags )
	LOOP
	
		INSERT INTO discussiontags ( discussionheadid, tags )
		VALUES
			( new_discussionheadid, param_tags [ I ] );
	
		IF
			param_tags [ I ] = 'Anime' 
			OR param_tags [ I ] = 'Watchroom' 
			OR param_tags [ I ] = 'Review' THEN
				INSERT INTO animediscussion ( discussionheadid, animeid, tag )
				VALUES
				( new_discussionheadid, param_animeid, param_tags [ I ] );
		
		END IF;
	
	END LOOP;
	
	SELECT * INTO discussionhead_row FROM discussionheads WHERE discussionheadid = new_discussionheadid;
	
	RETURN discussionhead_row;
END;
$$;


